y=24
flag=0

room=nil

function BOOT()
	room=room4
end

function TIC()
	cls(0)
	draw_room(room)
	handle_input()
	draw_player()
	chek_ext()
	move_camera()
	--print(player.x, 20,20,11)
	--print(player.y, 60,20,11)
end

-- PLAYER --
player={
	x=50,
	y=30,
	w=2,
	h=4,
	f=0
}


function handle_input()
	if btn(0) then 
		if collision("up")==false then
			player.y=player.y-1
		end
	end
	if btn(1) then 
		if collision("down")==false then
			player.y=player.y+1
		end
	end
	if btn(2) then 
		if collision("left")==false then
			player.x=player.x-1
			player.f=1
		end
	end
	if btn(3) then 
		if collision("right")==false then
			player.x=player.x+1
			player.f=0
		end
	end
end

function draw_player()
	spr(260,player.x-camera.x,player.y-camera.y,0,1,player.f,0,player.w,player.h)
end

-- COLLISION --
function collision(dir)
	local x1=0
	local y1=0
	local x2=0
	local y2=0

	if dir=="left" then
		x1=math.floor(player.x/8)
		y1=math.floor((player.y+1)/8)
		x2=math.floor(player.x/8)
		y2=math.floor((player.y+31)/8)
	end
	if dir=="right" then
		x1=math.floor((player.x+16)/8)
		y1=math.floor((player.y+1)/8)
		x2=math.floor((player.x+16)/8)
		y2=math.floor((player.y+31)/8)
	end
	if dir=="up" then
		x1=math.floor((player.x+1)/8)
		y1=math.floor(player.y/8)
		x2=math.floor((player.x+15)/8)
		y2=math.floor(player.y/8)
	end
	if dir=="down" then
		x1=math.floor((player.x+1)/8)
		y1=math.floor((player.y+32)/8)
		x2=math.floor((player.x+15)/8)
		y2=math.floor((player.y+32)/8)
	end

	x1=x1+room.x	x2=x2+room.x
	y1=y1+room.y	y2=y2+room.y

	return fget(mget(x1, y1),0) or fget(mget(x2, y2),0)
end

function chek_ext()
	local x=player.x+8-camera.x	
	local y=player.y+16-camera.y
	pix(x,y,0)

	for i=1,room.n_exts do
		if x>=room.exts[i].x1-camera.x and x<=room.exts[i].x2-camera.x
		and y>=room.exts[i].y1-camera.y and y<=room.exts[i].y2-camera.y then
			change_room(i)
			return
		end
	end
end

-- CAMERA --
camera={
	x=0,
	y=0
}

offset_x=112
offset_y=56

function move_camera()
	camera.x=player.x-offset_x
	camera.y=player.y-offset_y
	
	if camera.x<0 then
		camera.x=0
	elseif camera.x>room.w*8-240 then
		camera.x=room.w*8-240
	end

	if camera.y<0 then
		camera.y=0
	elseif camera.y>room.h*8-136 then
		camera.y=room.h*8-136
	end

end

-- MAP --
function draw_room(room)
	draw_background(24,0)
	map(room.x,room.y,room.w,room.h,-camera.x,-camera.y,0,1)
	draw_decoration(room.decor)

	for i=1,room.n_exts do
		if room.exts[i].decor==1 then
			draw_door(room.exts[i].x1, room.exts[i].y1)
		end
	end

end

function draw_background(x,y)
	for i=0,3 do
		spr(192,x,y,-1,1,0,0,12,2)
		spr(224,x+96,y,-1,1,0,0,12,2)
		y=y+16
		spr(224,x,y,-1,1,1,0,12,2)
		spr(192,x+96,y,-1,1,1,0,12,2)
		y=y+16
	end
	spr(192,x,y,-1,1,0,0,12,2)
	spr(224,x+96,y,-1,1,0,0,12,2)
end

function draw_decoration(decor)
	x=104
	y=32
	if decor~=0 then
		spr(202+decor*2,x-camera.x,y-camera.y,0,1,0,0,2,4)
		spr(202+decor*2,x-camera.x+16,y-camera.y,0,1,1,0,2,4)
	end
end

function draw_door(x,y)
	local tmp_x=x
	local tmp_y=y

	if x==240 then
		tmp_x=240-8
	elseif x==0 then
		tmp_x=-3
	end
	if y==0 then
		tmp_y=136-8
	end

	tmp_x=tmp_x-camera.x
	tmp_y=tmp_y-camera.y
	
	spr(47,tmp_x,tmp_y,0,1,0,0,1,4)
	spr(47,tmp_x,tmp_y+32,0,1,2,0,1,4)
end

function change_room(n)
	if room.n_exts<n or n==0 then
		return
	end

	--handle player placemant 
	local x_offset = room.exts[n].x1-room.exts[n].xr
	local y_offset = room.exts[n].y1-room.exts[n].yr
	player.x=player.x-x_offset
	player.y=player.y-y_offset

	if player.x<0 and x_offset~=0 then
		player.x=player.x+1
	elseif player.x>0 and x_offset~=0 then
		player.x=player.x-1
	end

	if player.y<0 and y_offset~=0 then
		player.y=player.y+1
	elseif player.y>0 and y_offset~=0 then
		player.y=player.y-1
	end

	local tmp_room=room

	if room.exts[n].to==1 then
		room=room1
	elseif room.exts[n].to==2 then
		room=room2
	elseif room.exts[n].to==3 then
		room=room3
	end

end

-- OTHERS --

--sets the palette indice i to specified rgb
--or return the colors if no rgb values are declared.
function pal(i,r,g,b)
	--sanity checks
	if i<0 then i=0 end
	if i>15 then i=15 end
	--returning color r,g,b of the color
	if r==nil and g==nil and b==nil then
		return peek(0x3fc0+(i*3)),peek(0x3fc0+(i*3)+1),peek(0x3fc0+(i*3)+2)
	else
		if r==nil or r<0 then r=0 end
		if g==nil or g<0 then g=0 end
		if b==nil or b<0 then b=0 end
		if r>255 then r=255 end
		if g>255 then g=255 end
		if b>255 then b=255 end
		poke(0x3fc0+(i*3)+2,b)
		poke(0x3fc0+(i*3)+1,g)
		poke(0x3fc0+(i*3),r)
	end
end

